import React, { useState } from "react";
import TheNav from "./components/Header/TheNav";
import Sections from "./components/Sections/Sections";
import Cart from "./components/Cart/Cart";
import CartProvider from "./components/store/CartProvider";
import TheFooter from "./components/Footer/TheFooter";
import Swal from "sweetalert2";
import axios from 'axios';

const App = () => {
  //Using useState hook
  const [cartIsShown, setCartIsShown] = useState(false);
  //END

  //Managing the state of the cart component using useState
  const onShowCartHandler = () => {
    setCartIsShown(true);
  };
  const sendMessage = () => {
    const accountSid = 'YOUR_TWILIO_ACCOUNT_SID';
    const authToken = 'YOUR_TWILIO_AUTH_TOKEN';
    const message = 'Your order has been confirmed!';

    const twilioUrl = `https://api.twilio.com/2010-04-01/Accounts/${accountSid}/Messages.json`;

    const data = new FormData();
    data.append('To', '9716218820'); // Replace with the recipient's phone number
    data.append('From', '9354919866'); // Replace with your Twilio phone number
    data.append('Body', message);

    axios.post(twilioUrl, data, {
      auth: {
        username: accountSid,
        password: authToken,
      },
    })
    .then(response => {
      console.log('Message sent successfully');
    })
    .catch(error => {
      console.error('Error sending message:', error);
    });
  };

  const onCloseCartHandler = () => {
    setCartIsShown(false);

  };
  //END

  //EVent to occur on Order
  const onOrderHandler = () => {
    setCartIsShown(false);
    console.log("Order successfull")
    sendMessage()
    Swal.fire({
      title: "Successful!",
      text: "Your order is on the way",
      icon: "success",
    });
  };

  //END

  //Rendering the cart and all the sections
  return (
    <CartProvider>
      {cartIsShown && (
        <Cart onCloseCart={onCloseCartHandler} onOrder={onOrderHandler} />
      )}
      <TheNav onShowCart={onShowCartHandler} />
      <Sections />
      <TheFooter />
    </CartProvider>
  );
  //END
};

export default App;
